function progress(a,b){var c=a*b.width()/100;b.find(".progressbar-value").animate({width:c},1200)}$(document).on("ready",function(){$(".progressbar").each(function(){var a=$(this),b=$(this).attr("data-value");progress(b,a)})}),$(function(){$("#progress-dropdown, #menu-right, #progress-25").hover(function(){$(".progressbar").each(function(){var a=$(this),b=$(this).attr("data-value");progress(b,a)})})});